import React, { useEffect, useRef, useState } from 'react';

// Main OpenStreetMap Component
const OpenStreetMap = () => {
  const mapContainerRef = useRef(null);
  const mapRef = useRef(null);
  const markersRef = useRef([]);
  const [coordinates, setCoordinates] = useState([51.505, -0.09]);
  const [zoom, setZoom] = useState(13);
  const [isMapLoaded, setIsMapLoaded] = useState(false);

  // Predefined locations
  const locations = {
    berlin: { center: [52.5100, 13.4000], zoom: 12, label: "Berlin" },
    bremen: { center: [53.075, 8.807], zoom: 13, label: "Bremen" },
    cologne: { center: [50.9383, 6.9599], zoom: 12, label: "Cologne" },
    dortmund: { center: [51.5142, 7.4652], zoom: 11, label: "Dortmund" },
    dresden: { center: [51.0493, 13.7381], zoom: 12, label: "Dresden" },
    duisburg: { center: [51.4349, 6.7595], zoom: 13, label: "Duisburg" },
    duesseldorf: { center: [51.225, 6.7763], zoom: 13, label: "Düsseldorf" },
    essen: { center: [51.4582, 7.0158], zoom: 12, label: "Essen" },
    frankfurt: { center: [50.1106, 8.6820], zoom: 11, label: "Tokyo" },
    hamburg: { center: [53.5503, 10.0006], zoom: 12, label: "Berlin" },
    hannover: { center: [52.3744, 9.7385], zoom: 13, label: "Sydney" },
    leipzig: { center: [51.3406, 12.3747], zoom: 12, label: "Berlin" },
    munich: { center: [48.1371, 11.5753], zoom: 12, label: "Berlin" },
    nuremberg: { center: [49.4538, 11.0772], zoom: 12, label: "Berlin" },
    stuttgart: { center: [48.7784, 9.1800], zoom: 12, label: "Berlin" }
  };

  // Load Leaflet scripts and styles
  useEffect(() => {
    // Skip if already loaded
    if (window.L) {
      setIsMapLoaded(true);
      return;
    }

    // Create link for Leaflet CSS
    const linkElement = document.createElement('link');
    linkElement.rel = 'stylesheet';
    linkElement.href = 'https://unpkg.com/leaflet@1.9.4/dist/leaflet.css';
    linkElement.integrity = 'sha256-p4NxAoJBhIIN+hmNHrzRCf9tD/miZyoHS5obTRR9BMY=';
    linkElement.crossOrigin = '';
    document.head.appendChild(linkElement);

    // Create script for Leaflet JS
    const scriptElement = document.createElement('script');
    scriptElement.src = 'https://unpkg.com/leaflet@1.9.4/dist/leaflet.js';
    scriptElement.integrity = 'sha256-20nQCchB9co0qIjJZRGuk2/Z9VM+kNiyxNV1lvTlZBo=';
    scriptElement.crossOrigin = '';
    scriptElement.onload = () => {
      setIsMapLoaded(true);
    };
    document.head.appendChild(scriptElement);

    // Cleanup function
    return () => {
      document.head.removeChild(linkElement);
      if (document.head.contains(scriptElement)) {
        document.head.removeChild(scriptElement);
      }
      if (mapRef.current) {
        mapRef.current.remove();
      }
    };
  }, []);

  // Initialize map when Leaflet is loaded
  useEffect(() => {
    if (!isMapLoaded || !mapContainerRef.current) return;

    // Initialize map if not already created
    if (!mapRef.current) {
      const L = window.L; // Get Leaflet from window object
      mapRef.current = L.map(mapContainerRef.current).setView(coordinates, zoom);
      
      // Add OpenStreetMap tile layer
      L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
        attribution: '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
      }).addTo(mapRef.current);

      // Add event listener for map clicks
      mapRef.current.on('click', handleMapClick);
      
      // Update coordinates and zoom when map is moved
      mapRef.current.on('moveend', () => {
        const center = mapRef.current.getCenter();
        setCoordinates([center.lat, center.lng]);
        setZoom(mapRef.current.getZoom());
      });
      
      // Add initial marker for current location
      addMarker(coordinates, "Starting Location");
    }
  }, [isMapLoaded]);

  // Update map view when coordinates or zoom changes
  useEffect(() => {
    if (mapRef.current) {
      mapRef.current.setView(coordinates, zoom);
    }
  }, [coordinates, zoom]);

  // Function to add a marker to the map
  const addMarker = (position, popupText) => {
    if (!mapRef.current || !window.L) return;
    
    const L = window.L; // Get Leaflet from window object
    const marker = L.marker(position).addTo(mapRef.current);
    if (popupText) {
      marker.bindPopup(popupText).openPopup();
    }
    
    markersRef.current.push(marker);
    return marker;
  };

  // Handle map clicks to add markers
  const handleMapClick = (e) => {
    const { lat, lng } = e.latlng;
    const position = [lat, lng];
    const popupText = `Marker at ${lat.toFixed(4)}, ${lng.toFixed(4)}`;
    addMarker(position, popupText);
  };

  // Function to clear all markers
  const clearMarkers = () => {
    if (mapRef.current) {
      markersRef.current.forEach(marker => {
        mapRef.current.removeLayer(marker);
      });
      markersRef.current = [];
    }
  };

  // Function to navigate to a location
  const navigateTo = (locationKey) => {
    const location = locations[locationKey];
    setCoordinates(location.center);
    setZoom(location.zoom);
    
    // Clear existing markers and add a new one
    clearMarkers();
    setTimeout(() => {
      addMarker(location.center, location.label);
    }, 100);
  };

  return (
    <div className="map-container">
      <div className="controls">
        <h2>OpenStreetMap Demo</h2>
        <div className="location-buttons">
          {Object.keys(locations).map(loc => (
            <button
              key={loc}
              onClick={() => navigateTo(loc)}
              className="location-button"
            >
              {locations[loc].label}
            </button>
          ))}
          <button onClick={clearMarkers} className="clear-button">
            Clear Markers
          </button>
        </div>
      </div>

      <div 
        ref={mapContainerRef} 
        id="map" 
        style={{ 
          height: '600px', 
          width: '100%',
          borderRadius: '8px',
          marginBottom: '10px'
        }}
      >
        {!isMapLoaded && <div className="loading">Loading map...</div>}
      </div>

      <div className="coordinates">
        Current position: {coordinates[0].toFixed(4)}, {coordinates[1].toFixed(4)} | Zoom: {zoom}
      </div>

      <style jsx>{`
        .map-container {
          max-width: 1200px;
          margin: 0 auto;
          padding: 20px;
          font-family: Arial, sans-serif;
        }
        
        .controls {
          margin-bottom: 20px;
        }
        
        h2 {
          color: #333;
          margin-bottom: 15px;
        }
        
        .location-buttons {
          display: flex;
          flex-wrap: wrap;
          gap: 10px;
          margin-bottom: 15px;
        }
        
        .location-button, .clear-button {
          padding: 8px 15px;
          border: none;
          border-radius: 4px;
          cursor: pointer;
          font-size: 14px;
          transition: background-color 0.3s;
        }
        
        .location-button {
          background-color: #4CAF50;
          color: white;
        }
        
        .location-button:hover {
          background-color: #45a049;
        }
        
        .clear-button {
          background-color: #f44336;
          color: white;
        }
        
        .clear-button:hover {
          background-color: #d32f2f;
        }
        
        .loading {
          display: flex;
          justify-content: center;
          align-items: center;
          height: 100%;
          font-size: 18px;
          color: #666;
        }
        
        .coordinates {
          margin-top: 10px;
          font-size: 14px;
          color: #666;
        }
      `}</style>
    </div>
  );
};

export default OpenStreetMap;
